import React, { useEffect } from 'react';
import './App.css';
import { AppBar, Tabs, Tab, makeStyles, createMuiTheme, Box, withStyles } from '@material-ui/core';
import { ThemeProvider } from '@material-ui/styles';
import { BrowserRouter as Router, Switch, Route, Link, useLocation} from "react-router-dom";
import About from './components/About';
import Home from './components/Home';
import Product from './components/Product';
import { Flex, Text } from 'rebass';
import Contact from './components/Contact';

import TopNav from './components/TopNav';

const useStyles = makeStyles(() => ({
  nav: {
    backgroundColor: 'rgba(255, 255, 255, 255)',
    color: "black",
    flexDirection: "row",
    justifyContent: "space-between",
    flexWrap: "wrap",
    fontWeight: 500,
    lineHeight: "10vh",
    boxShadow: "none",
  },
  root: {
    // backgroundImage: 'url("./image3.png")',
    // height: "100vh",
    background: "#FFFFFF"
    // backgroundSize: "cover"
  }
}))
const theme = createMuiTheme({
  palette: {
    secondary: {
      main: "#F1C833"
    }
  },
});

const CSSTabs = withStyles({
  root: {
    flexGrow: 1,
    maxWidth: 600,
  }
})(Tabs)
const routes: any[] = [{name: "Home", to: "/"}, {name: "Our Product", to: "/product"}, {name: "About us", to: "/about"}]
  
function App() {
  const location = useLocation();
  const classes = useStyles(undefined);
  const idx = routes.findIndex(route => route.to === location.pathname);
  return (
    <Box>
    <ThemeProvider theme={theme}>
        <TopNav />
        <Box className={classes.root}>
        <Switch>
          <Route exact path="/">
            <Home />
          </Route>
          <Route path="/about">
            <About/>
          </Route>
          <Route path="/product">
            <Product/>
          </Route>
        </Switch>
        </Box>
      </ThemeProvider>
      <Contact />
      <Flex style={{background: "#212020"}} height="80px" alignItems="center" justifyContent="center"> 
        <Text style={{color: "white"}}>Copyright © slickGoose. All rights reserved.</Text>
      </Flex>
      </Box>
  );
}


function ScrollToTop() {
  const { pathname } = useLocation();

  useEffect(() => {
    window.scrollTo(0, 0);
  }, [pathname]);

  return null;
}
export default () => (<Router><ScrollToTop/><App/></Router>);
