import React from 'react';
import { Heading, Flex } from "rebass";
import { Hero, Feature } from "react-landing-page";
import { Box, Typography, Button } from '@material-ui/core';
import Sunflower from './Sunflower';
import { Link } from "react-router-dom";
import {
    makeStyles, createStyles, Theme, withStyles,
  } from '@material-ui/core/styles';


const useStyles = makeStyles((theme: Theme) => createStyles({
bannerBox: {
    height: '60vh',
    padding: '5vh',
    textAlign: 'center',
    marginLeft: '9vh',
    [theme.breakpoints.down('md')]: {
        marginLeft: '8vh',
    },
    [theme.breakpoints.down('sm')]: {
        marginLeft: '5vh',
    },
    [theme.breakpoints.down('xs')]: {
        marginLeft: '2vh',
    },
},
banner: {
    width: '70%',
    [theme.breakpoints.down('md')]: {
        width: '100%',
    },
},
subtitle: {
    fontSize: '3vh',
    [theme.breakpoints.down('sm')]: {
        fontSize: '2vh',
    },
}
}));  
export default function Home(props: any) {
    const classes = useStyles(undefined);
    return (
        <Box>
            <Hero>
                <Box marginTop={1}>
                    <Heading textAlign="center" fontFamily={"inherit"} fontWeight={100}>
                        <Sunflower/>
                    </Heading>
                </Box>
                <Box>
                    <Typography className={classes.subtitle} style={{ textAlign: 'center' }}>
                        A cross-platform web app for digital learning.
                    </Typography>
                </Box>
                <Box className={classes.bannerBox}>
                    <img className={classes.banner} src="/home/mockup.png" alt="mockup" />
                </Box>
            </Hero>
            <Hero>
                <Flex backgroundColor="#E5E5E5" width={[1, 2/3, 1/2]} alignItems="center" flexDirection="column">
                    <Heading fontFamily={"inherit"} fontSize={[40, 50]} fontWeight={500}>OUR GOALS</Heading>
                    <Flex width={[1, 2/3]} justifyContent="center" marginTop={30}>
                        <Typography style={{fontSize: 26, textAlign: "center"}}>
                            Provide young students in under-developed countries with a better education platform to help them and their countries speed up their development.
                        </Typography>
                    </Flex>
                </Flex>
                <Flex width={[1, 2/3]} marginTop={100} alignItems="center" flexDirection="column">
                    <Heading fontFamily={"inherit"} fontSize={[40, 50]} fontWeight={500} textAlign="center">WHY <Sunflower/> ?</Heading>
                    <Flex flexDirection="row" justifyContent="space-between" flexWrap="wrap">
                        <Feature icon="📚" description="Designed to support both teaching and learning.">
                            Usable
                        </Feature>
                        <Feature icon="📈" description="Personalized analytic dashboard.">
                            Visual
                        </Feature>
                        <Feature icon="🔔" description="Detect potential mental health symptoms.">
                            Alert
                        </Feature>
                        <Feature icon="🧠" description="Minimalist interface design with simple English.">
                            Simple
                        </Feature>
                        <Feature icon="📱" description="Use anytime, anywhere, on any device.">
                            Responsive
                        </Feature>
                    </Flex>
                </Flex>
            </Hero>
        </Box>
    )
}
