import { Drawer, List, ListItem, Button } from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import React from 'react';
import CloseIcon from '@material-ui/icons/Close';
const useStyles = makeStyles({
  list: {
    width: 250,
  },
  fullList: {
    width: 'auto',
  },
});
const TopDrawer: React.FC = () => {
  const [opening, setOpenDrawer] = React.useState(false);
  const classes = useStyles(undefined);
  const toggleDrawer = (open: boolean) => (
    event: React.MouseEvent
  ) => {
  setOpenDrawer(true);
}
  return (
    <React.Fragment>
      <button onClick={toggleDrawer(true)}>
        Open
      </button>
      <Drawer anchor="top" open={opening} onClose={toggleDrawer(false)}>
        <List>
          <ListItem>
            <Button onClick={toggleDrawer(false)} startIcon={<CloseIcon />}>Close</Button>
          </ListItem>
          <ListItem>
            Home
          </ListItem>
          <ListItem>
            Home
          </ListItem>
          <ListItem>
            Home
          </ListItem>
        </List>
      </Drawer>
    </React.Fragment>
  );
};

export default TopDrawer;
