import { Drawer, List, ListItem, Button, IconButton } from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import React from 'react';
import CloseIcon from '@material-ui/icons/Close';
import MenuIcon from '@material-ui/icons/Menu';
const useStyles = makeStyles({
  drawerItem: {
    width: '100%',
  },
  section: {
    fontSize: '1.5rem',
    padding: '2vh',
    '&:hover': {
      backgroundColor: 'rgb(0,0,0, 0.2)',
    }
  }
});
const TopDrawer: React.FC = () => {
  const [opening, setOpenDrawer] = React.useState(false);
  const classes = useStyles(undefined);
  const toggleDrawer = () => {
  setOpenDrawer(!opening);
}
  return (
    <React.Fragment>
      <IconButton onClick={toggleDrawer}>
        <MenuIcon style={{ width: '2vh' }} />
      </IconButton>
      <Drawer anchor="top" open={opening} onClose={toggleDrawer}>
        <List className={classes.drawerItem}>
          <ListItem>
            <Button onClick={toggleDrawer} startIcon={<CloseIcon />}>Close</Button>
          </ListItem>
          <ListItem className={classes.section} divider>
            Home
          </ListItem >
          <ListItem className={classes.section} divider>
            Our Product
          </ListItem>
          <ListItem className={classes.section}>
            About Us
          </ListItem>
        </List>
      </Drawer>
    </React.Fragment>
  );
};

export default TopDrawer;
