import { Drawer, List, ListItem, Button } from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import React from 'react';
import CloseIcon from '@material-ui/icons/Close';
const useStyles = makeStyles({
  list: {
    width: '100%',
    '&:hover': {
      backgroundColor: 'gray',
    }
  },
});
const TopDrawer: React.FC = () => {
  const [opening, setOpenDrawer] = React.useState(false);
  const classes = useStyles(undefined);
  const toggleDrawer = () => {
  setOpenDrawer(!opening);
}
  return (
    <React.Fragment>
      <button onClick={toggleDrawer}>
        Open
      </button>
      <Drawer anchor="top" open={opening} onClose={toggleDrawer}>
        <List className={classes.list}>
          <ListItem>
            <Button onClick={toggleDrawer} startIcon={<CloseIcon />}>Close</Button>
          </ListItem>
          <ListItem>
            Home
          </ListItem>
          <ListItem>
            Our Product
          </ListItem>
          <ListItem>
            About Us
          </ListItem>
        </List>
      </Drawer>
    </React.Fragment>
  );
};

export default TopDrawer;
